window.addEventListener("scroll",(function(){let e=document.getElementById("header");window.scrollY>0?e.classList.add("fixed"):e.classList.remove("fixed")}));var hambButton=document.getElementById("hamb-bnt"),openSidebar=document.getElementById("sidebar");function openTab(e){document.querySelectorAll(".tab-content").forEach((e=>e.classList.remove("active")));const t=document.getElementById(e+"-content");t.classList.add("active");const n=document.getElementById(e);if(t.classList.contains("active")){document.querySelectorAll(".active-top").forEach((e=>e.classList.remove("active-top"))),n.classList.add("active-top")}else n.classList.remove("active-top")}hambButton.addEventListener("click",(function(){hambButton.classList.toggle("is-active"),openSidebar.classList.toggle("open")})),document.addEventListener("click",(function(e){var t=openSidebar.contains(e.target),n=hambButton.contains(e.target);t||n||(openSidebar.classList.remove("open"),hambButton.classList.remove("is-active"))}));const monthly=document.getElementById("monthly"),yearly=document.getElementById("yearly");monthly.addEventListener("click",(()=>openTab("monthly"))),yearly.addEventListener("click",(()=>openTab("yearly")));